import { Button, Grid, Typography } from "@mui/material"
import { Fragment, useEffect, useState } from "react"
import Select from 'react-select'
import Textarea from '@mui/joy/Textarea';
import axios from "axios";
import { useForm, SubmitHandler, Controller } from "react-hook-form"

const initialOptions = [
    { value: '', label: '선택' }
]

const Form = () => {
    const [options, setOptions] = useState(initialOptions)
    // const [building, setBuilding] = useState(initialOptions[0])
    const {
        control,
        handleSubmit,
        formState: { errors }
    } = useForm({
        defaultValues: {
            firstName: "",
            lastName: ""
        }
    })
    
    const onSubmit = (data) => console.log(data)

    useEffect(() => {
        axios.get('http://localhost:8080/api/building_list')
        .then((res) => {
            const newOptions = res.data.map((option) => ({ value: option, label: option}))
            setOptions([...newOptions])
        })
        .catch((error) => {
            console.error(error);
        })
    }, [])

    return (
        <Fragment>
            <Grid container>
                <Grid item xs={12} style={{ display: 'flex', justifyContent: 'center' }}>
                    <Typography variant="h3">포탈 찾기</Typography>
                </Grid>
                <Grid item xs={6}>
                    <Select
                        options={options}
                        defaultValue={initialOptions[0]}
                    />
                </Grid>
                <Grid item xs={12}>
                    <Controller
                        control={control}
                        rules={{required: true}}
                        render={({ field: { onChange, onBlur, value } }) => (
                            <Textarea 
                                name="Outlined"
                                placeholder="알고 있는 포탈을 입력해 주세요!"
                                variant="outlined"
                                minRows={3}
                            />
                        )}
                        name="portal"
                    />
                    {errors.portal && <Text>This is required.</Text>}
                </Grid>
                <Grid item xs={12} style={{ display: 'flex', 'justifyContent': 'end' }}>
                    <Button onClick={handleSubmit(onSubmit)}>등록</Button>
                </Grid>
            </Grid>
        </Fragment>
    )
}

export default Form
